mapper.insert:
insert into
BusinessBalanceRecord
(BusinessId,Amount,Status,Balance,RecordType,Operator,WithwardId,RelationNo,Remark,groupid, groupbeforebalance, groupafterbalance)
select #{businessid,jdbcType=INTEGER},
#{amount,jdbcType=DECIMAL},#{status,jdbcType=SMALLINT},b.BalancePrice,#{recordtype,jdbcType=SMALLINT},#{operator,jdbcType=NVARCHAR}
,#{withwardid,jdbcType=BIGINT},#{relationno,jdbcType=VARCHAR},#{remark,jdbcType=NVARCHAR}
,#{groupid,jdbcType=INTEGER}, #{groupbeforebalance,jdbcType=DECIMAL}, #{groupafterbalance,jdbcType=DECIMAL}
from dbo.business as b where Id=#{businessid,jdbcType=INTEGER} ;
select @@IDENTITY

mapper.sql:
    	1=1  
	 
	      AND bbr.OperateTime >=''${startDate}''
     
     
	      AND ''${endDate}'' > bbr.OperateTime 
     
     
	      AND bbr.RecordType =${recordType}
     
     
	       AND bbr.BusinessId=${businessID}
     
     
	       AND bbr.relationNo=''${relationNo}''

mapper.select:
SELECT bbr.[Id] ,bbr.[BusinessId] ,bbr.[Amount] ,bbr.[Status] ,bbr.[Balance] ,bbr.[RecordType] ,bbr.[Operator] ,bbr.[OperateTime] ,bbr.[WithwardId] ,bbr.[RelationNo] ,bbr.[Remark] ,bbr.[IsEnable] ,bbr.[groupid] ,bbr.[groupbeforebalance] ,bbr.[groupafterbalance] ,bfa.AccountNo ,bfa.OpenBank FROM [BusinessBalanceRecord] bbr WITH(NOLOCK) LEFT JOIN BusinessFinanceAccount bfa WITH(NOLOCK) ON bbr.BusinessId=bfa.BusinessId WHERE bbr.BusinessId=#{businessID,jdbcType=INTEGER}
AND bbr.[RecordType]=#{recordType,jdbcType=INTEGER}
AND bbr.[RelationNo]=#{relationNo,jdbcType=VARCHAR}
AND CONVERT(CHAR(10),bbr.OperateTime,120)>=CONVERT(CHAR(10),#{startDate,jdbcType=VARCHAR},120)
AND CONVERT(CHAR(10),#{endDate,jdbcType=VARCHAR},120) > CONVERT(CHAR(10),bbr.OperateTime,120)
ORDER BY bbr.Id DESC
