mapper.select:
		select 

    from GroupBusiness  b WITH ( NOLOCK )
		WHERE b.loginname = #{phoneNo,jdbcType=NVARCHAR}
		AND Password = #{password,jdbcType=NVARCHAR}

mapper.update:
update GroupBusiness set GroupBusiName = #{groupbusiname,jdbcType=NVARCHAR}, LoginName = #{loginname,jdbcType=VARCHAR},
Password = #{password,jdbcType=NVARCHAR},
ModifyName = #{modifyname,jdbcType=NVARCHAR}, ModifyTime = getdate() output Inserted.Id , Inserted.CreateName , Inserted.CreateTime , '修改集团名称' + Deleted.GroupBusiName + '为' + Inserted.GroupBusiName + ',登陆名' + Deleted.LoginName + '修改为' + Inserted.LoginName + ',修改密码' into dbo.GroupBusinessLog ( GroupBusinessId, OptName, OptTime, Remark ) where Id = #{id,jdbcType=INTEGER}

mapper.select:
		select

		from GroupBusiness (nolock) where 1=1 

        and id= #{id,jdbcType=INTEGER} 


        and GroupBusiName=#{groupBusinessName,jdbcType=NVARCHAR}


