mapper.update:
update ClienterLocation
set Longitude = #{longitude,jdbcType=DOUBLE},
  Latitude = #{latitude,jdbcType=DOUBLE},
  ClienterId = #{clienterid,jdbcType=INTEGER},
  CreateTime = #{createtime,jdbcType=TIMESTAMP}
where ID = #{id,jdbcType=BIGINT}

mapper.select:
 declare @beginId  BIGINT;
select @beginId=MAX(id) from ClienterLocation where CreateTime <dateadd(day,-1,getdate());
 declare @cliernterPoint geography ;
select @cliernterPoint=geography::Point(#{latitude},#{longitude},4326) ;
select a.ClienterId as id from dbo.ClienterLocation (nolock)   a
join dbo.clienter (nolock) b on a.ClienterId=b.Id
where  
a.ID>@beginId
and b.PushShanSongOrderSet=1
and b.DeliveryCompanyId=0
and b.WorkStatus=0
and datediff( second, CreateTime, getdate())  <#{minuteInfo}  
and round(geography::Point(ISNULL(Latitude,0),ISNULL(Longitude,0),4326).STDistance(@cliernterPoint),0) <#{distance}

group by ClienterId
