mapper.select:
    select 	b.Name BusinessName ,
        	case when b.PhoneNo2 is not null and b.PhoneNo2!='' then b.PhoneNo2
else b.PhoneNo end  BusinessPhoneNo ,
        	b.[Address] BusinessAddress ,
            og.OrderRegionOneName ,
            og.OrderRegionTwoName ,
            og.OrderCount ,
            og.Id GrabOrderId ,
            og.GrabOrderNo , 
            CONVERT(varchar(100),og.GrabTime,20) GrabTime,
            CONVERT(varchar(100),og.PickUpTime,20) PickUpTime,
            CONVERT(varchar(100),og.ActualDoneDate,20) ActualDoneDate ,
            og.[Status] ,
            b.Longitude businessLongitude,
            b.Latitude businessLatitude,
            b.City businessCityName,
            ( select    ISNULL(sum(a.OrderCommission),0)
              from      dbo.OrderGrabChild a ( nolock )
              where     a.GrabOrderId = #{grabOrderId,jdbcType=INTEGER}
            ) OrderCommission ,
            ( select    count(b.Id)
              from      dbo.OrderGrabChild b ( nolock )
              where     b.GrabOrderId = #{grabOrderId,jdbcType=INTEGER}
                        and b.[Status] = 1
            ) HadCompleteOrderGrab,
             case when og.Status = 2 then round(geography::Point(ISNULL(b.Latitude,0),ISNULL(b.Longitude,0),4326).STDistance(geography::Point(${clienterLatitude},${clienterLongitude},4326)),0) 
             else '' end as DistanceToBusiness 
     from   dbo.OrderGrab og ( nolock ) 
            join dbo.business b ( nolock ) on og.BusinessId = b.Id
     where og.Id = #{grabOrderId,jdbcType=INTEGER}

select b.Name BusinessName , b.PhoneNo BusinessPhoneNo , b.[Address] BusinessAddress , CONVERT(varchar(100),og.GrabTime,20) GrabTime, CONVERT(varchar(100),og.ActualDoneDate,20) ActualDoneDate , og.OrderRegionOneName , og.OrderRegionTwoName , og.OrderCount , og.Id GrabOrderId, og.GrabOrderNo, b.Longitude businessLongitude, b.Latitude businessLatitude, b.City businessCityName
,round(geography::Point(ISNULL(b.Latitude,0),ISNULL(b.Longitude,0),4326).STDistance(geography::Point(${clienterLatitude},${clienterLongitude},4326)),0) as DistanceToBusiness
from dbo.OrderGrab og ( nolock ) join dbo.business b ( nolock ) on og.BusinessId = b.Id where og.ClienterId = #{clienterId,jdbcType=INTEGER} and og.[Status] = #{status,jdbcType=INTEGER}

		 select  
isnull(sum(case when og.Status =  2 then og.OrderCount else 0 end),0) quHuoZhongTotal,
isnull(sum(case when og.Status =  4 then og.OrderCount else 0 end),0) peiSongZhongTotal
from   dbo.OrderGrab og ( nolock ) 
where  og.ClienterId = #{clienterId,jdbcType=INTEGER}
