mapper.select:
select o.[OrderNo] , ISNULL(b.Name, '匿名') + ':' + ISNULL(b.PhoneNo, '无') as BusinessInfo , ISNULL(c.TrueName, '匿名') + ':' + ISNULL(c.PhoneNo, '无') as ClienterInfo , CONVERT(VARCHAR(100), o.[PubDate], 120) as PubDate , ISNULL(CONVERT(VARCHAR(100), o.[ActualDoneDate], 120),'') as ActualDoneDate , o.[Amount] , ISNULL(o.[Amount], 0) + ISNULL(o.[OrderCount], 0) * ISNULL(o.[DistribSubsidy], 0) as TotalAmount , ISNULL(o.[OrderCommission], 0) as OrderCommission , ISNULL(o.[OrderCount], 0) as OrderCount , ISNULL(o.[DistribSubsidy], 0) as DistribSubsidy , ISNULL(o.[WebsiteSubsidy], 0) as WebsiteSubsidy , ISNULL(o.[Adjustment], 0) as Adjustment , ISNULL(o.[SettleMoney], 0) as SettleMoney from [order] o with ( nolock ) left join clienter c with ( nolock ) on c.Id = o.clienterId join business b with ( nolock ) on b.Id = o.businessId left join [group] g with ( nolock ) on g.id = o.OrderFrom join dbo.OrderOther oo ( nolock ) on o.Id = oo.OrderId where 1=1
and b.Name=#{businessName}
and b.PhoneNo=#{businessPhone}
and o.Status=#{orderStatus}
and c.TrueName=#{superManName}
and c.PhoneNo=#{superManPhone}
and o.PubDate>= '${orderPubStart} 00:00:00'
and o.PubDate<= '${orderPubEnd} 23:59:59'
and o.OrderFrom = #{groupId}
and b.City= #{businessCity}

SELECT top 1 o.[Id] ,o.[OrderNo] ,o.[PickUpAddress] ,o.PubDate ,o.[ReceviceName] ,o.[RecevicePhoneNo] ,o.[ReceviceAddress] ,o.[ActualDoneDate] ,o.[IsPay] ,o.[Amount] ,o.[OrderCommission] ,o.[DistribSubsidy] ,o.[WebsiteSubsidy] ,o.[Remark] ,o.[Status] ,o.[clienterId] ,o.[businessId] ,o.[ReceviceCity] ,o.[ReceviceLongitude] ,o.[ReceviceLatitude] ,o.[OrderFrom] ,o.[OriginalOrderId] ,o.[OriginalOrderNo] ,o.[Quantity] ,o.[Weight] ,o.[ReceiveProvince] ,o.[ReceiveArea] ,o.[ReceiveProvinceCode] ,o.[ReceiveCityCode] ,o.[ReceiveAreaCode] ,o.[OrderType] ,o.[KM] ,o.[GuoJuQty] ,o.[LuJuQty] ,o.[SongCanDate] ,o.[OrderCount] ,o.[CommissionRate] ,b.[City] BusinessCity ,b.Name BusinessName ,b.PhoneNo BusinessPhoneNo ,b.Address BusinessAddress ,c.PhoneNo ClienterPhoneNo ,c.TrueName ClienterTrueName ,c.TrueName ClienterName ,c.AccountBalance AccountBalance ,b.GroupId ,isnull(g.GroupName,'') as GroupName ,isnull(g.GroupName,'') as OrderFromName ,o.OriginalOrderNo ,oo.NeedUploadCount ,oo.HadUploadCount ,oo.ReceiptPic ,oo.DeductCommissionReason ,o.OtherCancelReason ,o.OriginalOrderNo ,o.IsEnable ,o.FinishAll ,ISNULL(oo.DeductCommissionType,0) DeductCommissionType ,ISNULL(oo.IsJoinWithdraw,0) IsJoinWithdraw ,ISNULL(oo.IsOrderChecked,1) IsOrderChecked ,ISNULL(o.SettleMoney,0) SettleMoney ,o.MealsSettleMode FROM [order] o WITH ( NOLOCK ) LEFT JOIN business b WITH ( NOLOCK ) ON b.Id = o.businessId LEFT JOIN clienter c WITH (NOLOCK) ON o.clienterId=c.Id LEFT JOIN OrderOther oo WITH (NOLOCK) ON oo.OrderId=o.Id LEFT JOIN [group] g WITH ( NOLOCK ) ON g.Id = o.orderfrom WHERE 1=1
and o.OrderNo = #{orderNo,jdbcType=NVARCHAR}
and o.id = #{orderId,jdbcType=INTEGER}

SELECT SUM(OrderCount) as pubCount, DATENAME(HOUR,o.PubDate) AS hour
FROM dbo.[order] (NOLOCK) o
WHERE businessId=#{businessId,jdbcType=INTEGER} AND
PubDate>=#{startTime,jdbcType=TIMESTAMP} AND PubDate<=#{endTime,jdbcType=TIMESTAMP} 
GROUP BY DATENAME(HOUR,o.PubDate) ORDER BY hour;

	declare @sdate DATETIME
set @sdate = '${monthInfo}-01'
declare @startDate date ;
declare @endDate date ;
set  @startDate  = dateadd(dd,-day(@sdate)+1,@sdate) ; 
set  @endDate=   dateadd(dd,1-day(@sdate),dateadd(m,1,@sdate));
select  a.MonthDate ,
        ISNULL(b.OrderCount, 0) as OrderCount ,
        ISNULL(b.ServiceClienterCount, 0) as ServiceClienterCount ,
        a.DateInfo
from    ( select    CONVERT(VARCHAR(100), DATEADD(dd, number, @sdate), 23) as MonthDate ,
                    CONVERT(VARCHAR(4), DATEPART(day,
                                                 DATEADD(dd, number, @sdate)))
                    + '日' as DateInfo
          from      master..spt_values
          where     type = 'P'
                    and DATEADD(dd, number, @sdate)  < DATEADD(mm, 1, @sdate)
        ) a
        left join ( select  SUM(OrderCount) as OrderCount ,
                            COUNT(distinct clienterId) as ServiceClienterCount ,
                            CONVERT(VARCHAR(100), PubDate, 23) as OrderDate
                    from    dbo.[order] (nolock)
                    where   Status = 1 and businessId=#{businessId} and [Platform] = #{platform} 
                    and PubDate>=@startDate and PubDate   <  @endDate
                    group by CONVERT(VARCHAR(100), PubDate, 23)
                  ) as b on a.MonthDate = b.OrderDate
where a.MonthDate  <=GETDATE()  
ORDER BY MonthDate DESC

	declare @sdate DATETIME
set @sdate = '${monthInfo}-01'
declare @startDate date ;
declare @endDate date ;
set  @startDate  = dateadd(dd,-day(@sdate)+1,@sdate) ; 
set  @endDate=   dateadd(dd,1-day(@sdate),dateadd(m,1,@sdate));

select  isnull(SUM(ISNULL(ordercount,0)),0) as OrderCount ,
        COUNT(distinct ISNULL(clienterId,0)) as ServiceClienterCount ,
        ISNULL(SUM(Amount),0)  as TotalAmount
from    dbo.[order] (nolock) 
where   Status = 1
        and PubDate >= @startDate and PubDate  < @endDate
        and businessId=#{businessId} and [Platform] = #{platform}

declare @sdate DATETIME
set @sdate = '${monthInfo}-01'
declare @startDate date ;
declare @endDate date ;
set  @startDate  = dateadd(dd,-day(@sdate)+1,@sdate) ; 
set  @endDate=   dateadd(dd,1-day(@sdate),dateadd(m,1,@sdate));

select  SUM(ISNULL(o.OrderCount,0)) as TotalOrderCount ,
	        ISNULL(SUM(CASE 
						WHEN o. FinishAll = 0 THEN 0 
						ELSE
						 CASE 
						  WHEN oo.AuditStatus=2 AND o.OrderCommission>o.SettleMoney THEN o.SettleMoney 
						  ELSE o.OrderCommission END  
						 END),0) TotalOrderCommission
from    dbo.[order] o WITH(NOLOCK)
join  OrderOther oo  WITH(NOLOCK) ON o.id=oo.OrderId
where   Status = 1
        and o.PubDate >= @startDate and o.PubDate  < @endDate
        and o.clienterId=#{clienterId}  
		and o.[Platform] = #{platform}

