mapper.select:
select  Id, Amount, CreateName, CreateTime, ModifyName, ModifyTime
from OrderTip
where     Amount= #{amount,jdbcType=DECIMAL} and id!=#{id,jdbcType=INTEGER}

{call Sp_CustomPage2015_V1(
'  id DESC',
' Id, Amount, CreateName, CreateTime, ModifyName, ModifyTime',
' 	OrderTip WITH (NOLOCK)',
'',
#{pageSize,mode=IN,jdbcType=INTEGER},
#{currentPage,mode=IN,jdbcType=INTEGER}
,1,
#{totalRecord,mode=OUT,jdbcType=INTEGER},
#{totalPage,mode=OUT,jdbcType=INTEGER}
)}

mapper.insert:
insert into OrderTip (Id, Amount, CreateName, 
  CreateTime, ModifyName, ModifyTime
  )
values (#{id,jdbcType=INTEGER}, #{amount,jdbcType=DECIMAL}, #{createname,jdbcType=NVARCHAR}, 
  #{createtime,jdbcType=TIMESTAMP}, #{modifyname,jdbcType=NVARCHAR}, #{modifytime,jdbcType=TIMESTAMP}
  )
