mapper.update:
update dbo.QuartzService
IsStart=#{status,jdbcType=INTEGER}, updateTime=getdate(), updateName=#{updateName,jdbcType=NVARCHAR},
where id=#{id,jdbcType=INTEGER}

mapper.select:
	 select 

    from QuartzService with(nolock)
    where IsStart = 1 

       and appSource = #{appSource,jdbcType=INTEGER}
